parameters.functionCode:
// Combine location data with current weather and forecast
const location = {
  name: $input.item.json.name,
  city: $input.item.json.city,
  country: $input.item.json.country,
  lat: $input.item.json.lat,
  lon: $input.item.json.lon,
  tags: $input.item.json.tags
};

// Extract current weather data
const currentWeather = $input.item.json;
const current = {
  temp: currentWeather.main.temp,
  feels_like: currentWeather.main.feels_like,
  temp_min: currentWeather.main.temp_min,
  temp_max: currentWeather.main.temp_max,
  pressure: currentWeather.main.pressure,
  humidity: currentWeather.main.humidity,
  wind_speed: currentWeather.wind.speed,
  wind_direction: currentWeather.wind.deg,
  clouds: currentWeather.clouds.all,
  visibility: currentWeather.visibility,
  weather_main: currentWeather.weather[0].main,
  weather_description: currentWeather.weather[0].description,
  weather_icon: currentWeather.weather[0].icon,
  rain_1h: currentWeather.rain ? currentWeather.rain['1h'] : 0,
  snow_1h: currentWeather.snow ? currentWeather.snow['1h'] : 0,
  sunrise: currentWeather.sys.sunrise,
  sunset: currentWeather.sys.sunset,
  timezone: currentWeather.timezone
};

// Get forecast data from the other branch
const forecastData = $('Get Weather Forecast').item.json;
const forecast = forecastData.list.map(item => ({
  dt: item.dt,
  temp: item.main.temp,
  feels_like: item.main.feels_like,
  weather_main: item.weather[0].main,
  weather_description: item.weather[0].description,
  weather_icon: item.weather[0].icon,
  pop: item.pop, // probability of precipitation
  wind_speed: item.wind.speed
}));

// Analyze weather conditions
const conditions = {
  isRaining: current.weather_main.toLowerCase().includes('rain') || current.rain_1h > 0,
  isSnowing: current.weather_main.toLowerCase().includes('snow') || current.snow_1h > 0,
  isClear: current.weather_main.toLowerCase().includes('clear'),
  isCloudy: current.weather_main.toLowerCase().includes('cloud'),
  isStormy: current.weather_main.toLowerCase().includes('thunderstorm'),
  isFoggy: current.weather_main.toLowerCase().includes('fog') || current.weather_main.toLowerCase().includes('mist'),
  isHot: current.temp > 85,
  isCold: current.temp < 40,
  isWindy: current.wind_speed > 15,
  isHumid: current.humidity > 70,
  isDaytime: Date.now() / 1000 > current.sunrise && Date.now() / 1000 < current.sunset,
  rainSoon: forecast.slice(0, 3).some(item => item.weather_main.toLowerCase().includes('rain') || item.pop > 0.5),
  tempDropping: forecast[0].temp > forecast[2].temp + 5,
  tempRising: forecast[2].temp > forecast[0].temp + 5
};

// Create triggers based on conditions
const triggers = [];

if (conditions.isRaining) triggers.push('rain');
if (conditions.isSnowing) triggers.push('snow');
if (conditions.isClear && conditions.isDaytime) triggers.push('sunny');
if (conditions.isClear && !conditions.isDaytime) triggers.push('clear-night');
if (conditions.isCloudy) triggers.push('cloudy');
if (conditions.isStormy) triggers.push('storm');
if (conditions.isFoggy) triggers.push('fog');
if (conditions.isHot) triggers.push('hot');
if (conditions.isCold) triggers.push('cold');
if (conditions.isWindy) triggers.push('windy');
if (conditions.isHumid) triggers.push('humid');
if (conditions.rainSoon) triggers.push('rain-soon');
if (conditions.tempDropping) triggers.push('cooling');
if (conditions.tempRising) triggers.push('warming');

// Return the combined data
return {
  location,
  current,
  forecast: forecast.slice(0, 3), // Next 9 hours (3 x 3-hour intervals)
  conditions,
  triggers,
  timestamp: new Date().toISOString()
};

