parameters.jsCode:
const products = [...$('Setup defaults').last().json.products];

for (const item of $input.all()) {
  if (
    `${item.json.title} ${item.json.body_html}`.toLowerCase()
      .includes(
        $('On form submission').first().json['Search term'].toLowerCase()
      )
  ) {
    products.push(item.json);  
  }
}

return {
  json: {
    products,
    page: $('Setup defaults').last().json.page + 1
  }
}

parameters.inputSchema:
{
    "type": "object",
    "properties": {
        "image_prompt": {
            "type": "string",
            "description": "The image prompt for the AI image generation"
        },
        "dialogue": {
            "type": "string",
            "description": "The dialogue that the main character should be saying to the camera"
        },
        "video_prompt": {
            "type": "string",
            "description": "The video prompt, that we are going to use to generate the video starting and finishing with the generated image"
        },
        "sex": {
            "type": "string",
            "description": "The sex of the person on the attached image"
        }
    },
    "required": [
        "image_prompt",
        "dialogue",
        "video_prompt",
        "sex"
    ]
}

